mapper.select:
SELECT
-- 级联表的字段 ,kecheng.laoshi_id as laoshiId ,kecheng.kecheng_name as kechengName ,kecheng.kecheng_content as kechengContent ,yonghu.username as username ,yonghu.password as password ,yonghu.name as name ,yonghu.phone as phone ,yonghu.id_number as idNumber ,yonghu.sex_types as sexTypes ,yonghu.my_photo as myPhoto ,yonghu.nation as nation ,yonghu.politics_types as politicsTypes ,yonghu.birthplace as birthplace ,yonghu.wupin_types as wupinTypes ,yonghu.banji_id as banjiId ,yonghu.yonghu_types as yonghuTypes
FROM chengji a left JOIN kecheng kecheng ON a.kecheng_id = kecheng.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.yonghu_id = #{params.yonghuId}
and a.exam_name like CONCAT('%',#{params.examName},'%')
and a.kecheng_id = #{params.kechengId}
and a.fraction >= #{params.fractionStart}
and a.fraction <= #{params.fractionEnd}
and kecheng.laoshi_id = #{params.laoshiId}
and kecheng.kecheng_name like CONCAT('%',#{params.kechengName},'%')
and kecheng.kecheng_content like CONCAT('%',#{params.kechengContent},'%')
and yonghu.username like CONCAT('%',#{params.username},'%')
and yonghu.password like CONCAT('%',#{params.password},'%')
and yonghu.name like CONCAT('%',#{params.name},'%')
and yonghu.phone like CONCAT('%',#{params.phone},'%')
and yonghu.id_number like CONCAT('%',#{params.idNumber},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.nation like CONCAT('%',#{params.nation},'%')
and yonghu.politics_types = #{params.politicsTypes}
and yonghu.birthplace like CONCAT('%',#{params.birthplace},'%')
and yonghu.wupin_types = #{params.wupinTypes}
and yonghu.banji_id = #{params.banjiId}
and yonghu.yonghu_types = #{params.yonghuTypes}
order by a.id desc
